static int mapMode(cchar *mode)
{
    int     omode;

    omode = O_BINARY;
    if (strchr(mode, 'r')) {
        omode |= O_RDONLY;
    }
    if (strchr(mode, 'w')) {
        omode |= O_CREAT | O_WRONLY | O_TRUNC;
    }
    if (strchr(mode, 'a')) {
        omode |= O_WRONLY | O_APPEND;
        omode &= ~O_TRUNC;
    }
    if (strchr(mode, '+')) {
        /* Append to existing content */
        omode &= ~O_TRUNC;
    }
    if (strchr(mode, 't')) {
        /* Text mode */
        omode &= ~O_BINARY;
    }
#if O_EXLOCK
    if (strchr(mode, 'l')) {
        /* Exclusive lock */
        omode |= O_EXLOCK;
    }
#endif
#if O_SHLOCK
    if (strchr(mode, 's')) {
        /* Shared lock */
        omode |= O_SHLOCK;
    }
#endif
    if (strchr(mode, 'c')) {
        /* Create - must not exist prior */
        omode |= O_CREAT | O_EXCL;
    }
    return omode;
}


