{
    assert(fun);
    assert(byteCode);
    assert(len >= 0);
    assert(debug == 0 || debug->magic == EJS_DEBUG_MAGIC);

    fun->body.code = ejsCreateCode(ejs, fun, module, byteCode, len, debug);
    return 0;
}


