static EjsBoolean *isFinite(Ejs *ejs, EjsNumber *np, int argc, EjsObj **argv)
{
    if (np->value == ((EjsNumber*) ESV(nan))->value || np->value == ((EjsNumber*) ESV(infinity))->value || 
            np->value == ((EjsNumber*) ESV(negativeInfinity))->value) {
        return ESV(false);
    }
    return ESV(true);
}


